import React from 'react';


class StorePicker extends React.Component {
    constructor(props) {
      super(props);
      // initialize state here
      this.state = {
        name: "erwin",
        title: "The Store Picker"
      }
    }
    render() {
      return (
        <React.Fragment>
            <form className="store-selector-form"> 
               <h2>Please Enter A Store Name</h2>
               <input className="input-selector" type="text" required placeholder="Store Name" />
               <button className="submit-button" type="submit">Visit Store</button>
            </form>
        </React.Fragment>
      );
    }
  };

export default StorePicker;
